import React from 'react';
import { useAuth } from '../context/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout();
      toast.success('Logged out successfully');
      navigate('/login');
    } catch (error) {
      toast.error('Logout failed');
    }
  };

  const getRoleBadgeClass = (role) => {
    switch (role) {
      case 'super_admin':
        return 'badge-danger';
      case 'admin':
        return 'badge-warning';
      case 'user':
        return 'badge-primary';
      default:
        return 'badge-secondary';
    }
  };

  const formatRole = (role) => {
    return role.split('_').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  return (
    <div className="dashboard-container">
      {/* Navigation Header */}
      <div className="dashboard-header">
        <div className="container">
          <div className="d-flex justify-content-between align-items-center">
            <h1 className="dashboard-title">RBAC Dashboard</h1>
            <div className="d-flex align-items-center gap-3">
              <span className="welcome-text">
                Welcome, <strong>{user?.name}</strong>
              </span>
              <span className={`badge ${getRoleBadgeClass(user?.role)}`}>
                {formatRole(user?.role || 'user')}
              </span>
              <button 
                onClick={handleLogout}
                className="btn btn-outline btn-sm"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="container">
        <div className="dashboard-content">
          {/* Welcome Card */}
          <div className="card mb-4">
            <div className="card-header">
              <h2 className="card-title">Welcome to RBAC System</h2>
            </div>
            <div className="card-content">
              <p className="mb-3">
                You are logged in as <strong>{formatRole(user?.role || 'user')}</strong>. 
                Your permissions depend on your assigned role.
              </p>
              <div className="role-info">
                <h4>Your Current Permissions:</h4>
                <ul className="permission-list">
                  <li>âœ“ View items</li>
                  <li>âœ“ View your profile</li>
                  {(user?.role === 'admin' || user?.role === 'super_admin') && (
                    <>
                      <li>âœ“ Create items</li>
                      <li>âœ“ Update items</li>
                      <li>âœ“ Delete items</li>
                    </>
                  )}
                  {user?.role === 'super_admin' && (
                    <>
                      <li>âœ“ Manage user accounts</li>
                      <li>âœ“ Change user roles</li>
                      <li>âœ“ System administration</li>
                    </>
                  )}
                </ul>
              </div>
            </div>
          </div>

          {/* Quick Actions */}
          <div className="card mb-4">
            <div className="card-header">
              <h3 className="card-title">Quick Actions</h3>
            </div>
            <div className="card-content">
              <div className="action-grid">
                <Link to="/items" className="action-card">
                  <div className="action-icon">ðŸ“‹</div>
                  <h4>View Items</h4>
                  <p>Browse all available items in the system</p>
                </Link>

                {(user?.role === 'admin' || user?.role === 'super_admin') && (
                  <Link to="/items/create" className="action-card">
                    <div className="action-icon">âž•</div>
                    <h4>Create Item</h4>
                    <p>Add a new item with image and details</p>
                  </Link>
                )}

                {user?.role === 'super_admin' && (
                  <Link to="/users" className="action-card">
                    <div className="action-icon">ðŸ‘¥</div>
                    <h4>Manage Users</h4>
                    <p>View and manage user accounts and roles</p>
                  </Link>
                )}
              </div>
            </div>
          </div>

          {/* System Statistics (for admins) */}
          {(user?.role === 'admin' || user?.role === 'super_admin') && (
            <div className="card">
              <div className="card-header">
                <h3 className="card-title">System Overview</h3>
              </div>
              <div className="card-content">
                <div className="stats-grid">
                  <div className="stat-card">
                    <div className="stat-number">â€”</div>
                    <div className="stat-label">Total Items</div>
                  </div>
                  <div className="stat-card">
                    <div className="stat-number">â€”</div>
                    <div className="stat-label">Active Users</div>
                  </div>
                  {user?.role === 'super_admin' && (
                    <div className="stat-card">
                      <div className="stat-number">â€”</div>
                      <div className="stat-label">Total Users</div>
                    </div>
                  )}
                </div>
                <p className="mt-3 text-center">
                  <em>Statistics will be loaded when you navigate to respective sections</em>
                </p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
