import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import apiService from '../services/api';

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [pagination, setPagination] = useState({
    currentPage: 1,
    totalPages: 1,
    totalUsers: 0,
    limit: 10
  });
  const [filters, setFilters] = useState({
    search: '',
    role: '',
    isActive: ''
  });

  // Fetch users
  const fetchUsers = async () => {
    try {
      setLoading(true);
      const params = {
        page: pagination.currentPage,
        limit: pagination.limit,
        ...(filters.search && { search: filters.search }),
        ...(filters.role && { role: filters.role }),
        ...(filters.isActive !== '' && { isActive: filters.isActive })
      };

      const response = await apiService.getUsers(params);
      if (response.success) {
        setUsers(response.data.users);
        setPagination(response.data.pagination);
      }
    } catch (error) {
      toast.error('Failed to fetch users');
    } finally {
      setLoading(false);
    }
  };

  // Fetch user statistics
  const fetchStats = async () => {
    try {
      const response = await apiService.getUserStats();
      if (response.success) {
        setStats(response.data);
      }
    } catch (error) {
      console.error('Failed to fetch stats:', error);
    }
  };

  // Update user role
  const handleRoleChange = async (userId, newRole) => {
    try {
      const response = await apiService.updateUserRole(userId, newRole);
      if (response.success) {
        toast.success('User role updated successfully');
        fetchUsers();
      }
    } catch (error) {
      toast.error('Failed to update user role');
    }
  };

  // Toggle user activation
  const handleToggleActivation = async (userId) => {
    try {
      const response = await apiService.toggleUserActivation(userId);
      if (response.success) {
        toast.success(response.message || 'User status updated successfully');
        fetchUsers();
        fetchStats();
      }
    } catch (error) {
      toast.error('Failed to update user status');
    }
  };

  // Handle filter changes
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
    setPagination(prev => ({ ...prev, currentPage: 1 }));
  };

  // Handle page change
  const handlePageChange = (newPage) => {
    setPagination(prev => ({ ...prev, currentPage: newPage }));
  };

  useEffect(() => {
    fetchUsers();
  }, [pagination.currentPage, filters]);

  useEffect(() => {
    fetchStats();
  }, []);

  const getRoleBadgeClass = (role) => {
    switch (role) {
      case 'super_admin':
        return 'badge-danger';
      case 'admin':
        return 'badge-warning';
      default:
        return 'badge-info';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="container">
      <div className="page-header">
        <h1 className="page-title">üë• User Management</h1>
        <p className="page-subtitle">Manage user accounts, roles, and permissions</p>
      </div>

      {/* User Statistics */}
      {stats && (
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))', 
          gap: '20px', 
          marginBottom: '30px' 
        }}>
          <div className="card" style={{ 
            textAlign: 'center', 
            padding: '25px',
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            color: 'white',
            borderRadius: '12px',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
          }}>
            <div style={{ fontSize: '2.5em', marginBottom: '5px', fontWeight: 'bold' }}>
              {stats.totalUsers || 0}
            </div>
            <div style={{ fontSize: '0.9em', opacity: 0.9, textTransform: 'uppercase', letterSpacing: '1px' }}>
              Total Users
            </div>
          </div>
          <div className="card" style={{ 
            textAlign: 'center', 
            padding: '25px',
            background: 'linear-gradient(135deg, #11998e 0%, #38ef7d 100%)',
            color: 'white',
            borderRadius: '12px',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
          }}>
            <div style={{ fontSize: '2.5em', marginBottom: '5px', fontWeight: 'bold' }}>
              {stats.activeUsers || 0}
            </div>
            <div style={{ fontSize: '0.9em', opacity: 0.9, textTransform: 'uppercase', letterSpacing: '1px' }}>
              Active Users
            </div>
          </div>
          <div className="card" style={{ 
            textAlign: 'center', 
            padding: '25px',
            background: 'linear-gradient(135deg, #ee0979 0%, #ff6a00 100%)',
            color: 'white',
            borderRadius: '12px',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
          }}>
            <div style={{ fontSize: '2.5em', marginBottom: '5px', fontWeight: 'bold' }}>
              {stats.inactiveUsers || 0}
            </div>
            <div style={{ fontSize: '0.9em', opacity: 0.9, textTransform: 'uppercase', letterSpacing: '1px' }}>
              Inactive Users
            </div>
          </div>
          <div className="card" style={{ 
            textAlign: 'center', 
            padding: '25px',
            background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
            color: 'white',
            borderRadius: '12px',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
          }}>
            <div style={{ fontSize: '2.5em', marginBottom: '5px', fontWeight: 'bold' }}>
              {stats.adminCount || 0}
            </div>
            <div style={{ fontSize: '0.9em', opacity: 0.9, textTransform: 'uppercase', letterSpacing: '1px' }}>
              Admins
            </div>
          </div>
        </div>
      )}

      {/* Filters */}
      <div className="card" style={{ marginBottom: '20px', borderRadius: '12px', boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)' }}>
        <div className="card-content">
          <div style={{ marginBottom: '15px', display: 'flex', alignItems: 'center', gap: '10px' }}>
            <span style={{ fontSize: '1.1em', fontWeight: '600', color: '#374151' }}>üîç Filter Users</span>
          </div>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))', gap: '20px' }}>
            <div className="form-group" style={{ margin: 0 }}>
              <label className="form-label" style={{ fontWeight: '500', marginBottom: '8px', display: 'block', color: '#374151' }}>
                üîé Search
              </label>
              <input
                type="text"
                name="search"
                className="form-input"
                placeholder="Search by name or email..."
                value={filters.search}
                onChange={handleFilterChange}
                style={{ 
                  padding: '10px 12px',
                  borderRadius: '8px',
                  border: '1px solid #d1d5db',
                  fontSize: '0.95em',
                  transition: 'border-color 0.2s'
                }}
              />
            </div>
            <div className="form-group" style={{ margin: 0 }}>
              <label className="form-label" style={{ fontWeight: '500', marginBottom: '8px', display: 'block', color: '#374151' }}>
                üë§ Role Filter
              </label>
              <select
                name="role"
                className="form-input"
                value={filters.role}
                onChange={handleFilterChange}
                style={{ 
                  padding: '10px 12px',
                  borderRadius: '8px',
                  border: '1px solid #d1d5db',
                  fontSize: '0.95em',
                  cursor: 'pointer'
                }}
              >
                <option value="">All Roles</option>
                <option value="user">User</option>
                <option value="admin">Admin</option>
                <option value="super_admin">Super Admin</option>
              </select>
            </div>
            <div className="form-group" style={{ margin: 0 }}>
              <label className="form-label" style={{ fontWeight: '500', marginBottom: '8px', display: 'block', color: '#374151' }}>
                ‚ö° Status Filter
              </label>
              <select
                name="isActive"
                className="form-input"
                value={filters.isActive}
                onChange={handleFilterChange}
                style={{ 
                  padding: '10px 12px',
                  borderRadius: '8px',
                  border: '1px solid #d1d5db',
                  fontSize: '0.95em',
                  cursor: 'pointer'
                }}
              >
                <option value="">All Status</option>
                <option value="true">‚úì Active</option>
                <option value="false">‚úó Inactive</option>
              </select>
            </div>
          </div>
        </div>
      </div>

      {/* Users Table */}
      <div className="card">
        <div className="card-content">
          {loading ? (
            <div style={{ textAlign: 'center', padding: '40px' }}>
              <p>Loading users...</p>
            </div>
          ) : users.length === 0 ? (
            <div style={{ textAlign: 'center', padding: '40px' }}>
              <p>No users found</p>
            </div>
          ) : (
            <>
              <div style={{ overflowX: 'auto' }}>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                  <thead>
                    <tr style={{ borderBottom: '2px solid #e5e7eb' }}>
                      <th style={{ padding: '12px', textAlign: 'left' }}>Name</th>
                      <th style={{ padding: '12px', textAlign: 'left' }}>Email</th>
                      <th style={{ padding: '12px', textAlign: 'left' }}>Role</th>
                      <th style={{ padding: '12px', textAlign: 'center' }}>Status</th>
                      <th style={{ padding: '12px', textAlign: 'left' }}>Created</th>
                      <th style={{ padding: '12px', textAlign: 'center' }}>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.map((user) => (
                      <tr key={user._id} style={{ borderBottom: '1px solid #e5e7eb' }}>
                        <td style={{ padding: '12px' }}>{user.name}</td>
                        <td style={{ padding: '12px' }}>{user.email}</td>
                        <td style={{ padding: '12px' }}>
                          <select
                            className="form-input"
                            value={user.role}
                            onChange={(e) => handleRoleChange(user._id, e.target.value)}
                            style={{ padding: '5px 10px', fontSize: '0.9em' }}
                          >
                            <option value="user">User</option>
                            <option value="admin">Admin</option>
                            <option value="super_admin">Super Admin</option>
                          </select>
                        </td>
                        <td style={{ padding: '12px', textAlign: 'center' }}>
                          <span
                            className={`badge ${user.isActive ? 'badge-success' : 'badge-secondary'}`}
                            style={{ padding: '5px 10px', fontSize: '0.85em' }}
                          >
                            {user.isActive ? 'Active' : 'Inactive'}
                          </span>
                        </td>
                        <td style={{ padding: '12px', fontSize: '0.9em', color: '#6b7280' }}>
                          {formatDate(user.createdAt)}
                        </td>
                        <td style={{ padding: '12px', textAlign: 'center' }}>
                          <button
                            className={`btn ${user.isActive ? 'btn-danger' : 'btn-success'}`}
                            onClick={() => handleToggleActivation(user._id)}
                            style={{ padding: '5px 15px', fontSize: '0.85em' }}
                          >
                            {user.isActive ? 'Deactivate' : 'Activate'}
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Pagination */}
              {pagination.totalPages > 1 && (
                <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '10px', marginTop: '20px', padding: '20px 0' }}>
                  <button
                    className="btn btn-secondary"
                    onClick={() => handlePageChange(pagination.currentPage - 1)}
                    disabled={!pagination.hasPrev}
                    style={{ padding: '8px 16px' }}
                  >
                    Previous
                  </button>
                  <span style={{ color: '#6b7280' }}>
                    Page {pagination.currentPage} of {pagination.totalPages}
                  </span>
                  <button
                    className="btn btn-secondary"
                    onClick={() => handlePageChange(pagination.currentPage + 1)}
                    disabled={!pagination.hasNext}
                    style={{ padding: '8px 16px' }}
                  >
                    Next
                  </button>
                </div>
              )}

              <div style={{ textAlign: 'center', marginTop: '10px', color: '#6b7280', fontSize: '0.9em' }}>
                Showing {users.length} of {pagination.totalUsers} users
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserManagement;
