import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import apiService from '../services/api';
import { hasPermissions } from '../utils/permissions';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [pagination, setPagination] = useState({});

  const canCreate = hasPermissions(user?.role, ['create']);
  const canUpdate = hasPermissions(user?.role, ['update']);
  const canDelete = hasPermissions(user?.role, ['delete']);

  const handleLogout = async () => {
    try {
      await logout();
      toast.success('Logged out successfully');
      navigate('/login');
    } catch (error) {
      toast.error('Logout failed');
    }
  };

  const getRoleBadgeClass = (role) => {
    switch (role) {
      case 'super_admin':
        return 'badge-danger';
      case 'admin':
        return 'badge-warning';
      case 'user':
        return 'badge-primary';
      default:
        return 'badge-secondary';
    }
  };

  const formatRole = (role) => {
    return role.split('_').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  return (
    <div className="dashboard-container">
      {/* Navigation Header */}
      <div className="dashboard-header" style={{ 
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        padding: '20px 0',
        marginBottom: '30px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
      }}>
        <div className="container">
          <div className="d-flex justify-content-between align-items-center">
            <h1 className="dashboard-title" style={{ color: 'white', fontSize: '1.8em', margin: 0 }}>üéØ RBAC Dashboard</h1>
            <div className="d-flex align-items-center gap-3">
              <span className="welcome-text" style={{ color: 'white', fontSize: '1.05em' }}>
